mapper.insert:

insert into sys_right (name, code, pcode, 
  update_time, description, url, 
  icon, type, status, 
  application_status, path, orders
  )
values (#{name,jdbcType=VARCHAR}, #{code,jdbcType=VARCHAR}, #{pcode,jdbcType=BIGINT}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{description,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{icon,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, 
  #{applicationStatus,jdbcType=VARCHAR}, #{path,jdbcType=VARCHAR}, #{orders,jdbcType=INTEGER}
  )

mapper.select:
    select * from sys_right aa


		and aa.id = #{id}


		and aa.name = #{name}


		and aa.code = #{code}


		and aa.pcode = #{pcode}


		and aa.update_time = #{updateTime}


		and aa.description = #{description}


		and aa.url = #{url}


		and aa.icon = #{icon}


		and aa.type = #{type}


		and aa.status = #{status}


		and aa.application_status = #{applicationStatus}


		and aa.path = #{path}


		and aa.orders = #{orders}
